    }
html body .home_specials_grid .special {
    border-color: rgba( 255, 255, 255, 0.1);
    }
html body .home_block_footer {
    background: none;
    padding: 2px 0 0;
    }
html body .home_content.single {
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom: none;
    }
html body .home_content.single a.screenshot {
    width: 212px;
    }
html body .home_content.single_buttonbar {
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-top: none;
    margin-top: 0;
    }


/*
 *  Games
 *  ---------------------------------------------
 */

 html body #agegate_box select {
     padding-right: 30px;
     }

html body .rightcol.game_meta_data .block,
html body .explore_rightcol_box {
    border: 1px solid rgba(255, 255, 255, 0.1);
    }
html body .rightcol.game_meta_data .block {
    padding: 14px;
    }
html body .rightcol.game_meta_data .block_content_inner {
    border: none;
    }

html body .review_box {
    position: relative;
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.05);
    }
    html body .review_box_content {
        position: relative;
        }
    html body .review_box .leftcol {
        float: none;
        width: 300px;
        padding: 20px;
        margin: 0;
        opacity: 1;
        }
    html body .friend_reviews .review_box .leftcol {
        margin-top: 0;
        }
    html body .review_box .rightcol {
        float: none;
        width: auto;
        padding: 0 20px;
        margin: 0;
        overflow: inherit;
        position: static;
        }
    html body .review_box .vote_header {
        float: right;
        width: 230px;
        margin: 0;
        position: absolute;
        top: 20px;
        right: 20px;
        }
    html body .review_box .content {
        margin: 0;
        }
    html body .review_box blockquote.bb_blockquote,
    html body .review_box .bb_wysiwyg blockquote {
        margin: 0 0 10px;
        }
    html body .review_box .posted {
        margin-left: 0;
        margin-right: 0;
        }
    html body .review_box .postedDate {
        clear: both;
        display: block;
        }
        html body .review_box .postedDate br {
            display: none;
            }
    html body .review_box .control_block {
        padding-left: 0;
        padding-right: 0;
        }

html body .review_page_content {
    }
    html body .review_page_content #leftContents {
        background: rgba(0, 0, 0, 0.2);
        border: 1px solid rgba(255, 255, 255, 0.05);
        }
        html body .review_page_content .ratingSummaryBlock {
            padding: 20px 20px 0;
            }
        html body .review_page_content .review_area {
            padding: 20px;
            }
        html body .review_page_content .commentthread_area {
            padding: 0 20px 20px;
            }

html body .game_description_column .block_header {
    border: none;
    }

html body .game_area_play_stats {
    padding-top: 20px;
    }
html body .already_owned_actions {
    display: inline-block;
    vertical-align: top;
    padding-top: 0;
    }
html body .game_area_already_owned_btn {
    float: none;
    margin-right: 0;
    display: inline-block;
    vertical-align: top;
    }
    html body .game_area_already_owned_btn a[href="http://store.steampowered.com/about/"] {
        /* remove 'Install Steam' link */
        display: none;
        }
html body .already_owned_actions + .game_area_already_owned_btn {
    margin-left: 12px;
    }
html body .dynamiclink_box {
    margin-right: 0;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    }

html body #review_recent_events_container {
    box-shadow: none;
    }
html body .user_reviews_header {
    background: none;
    }
    html body #review_histograms_container {
        box-shadow: none;
        }
        html body .review_histogram_section .user_reviews_summary_bar {
            border-bottom: none;
            }
        html body .review_histogram_section.recent {
            box-shadow: none;
            }
    html body .user_reviews_summary_bar .game_review_summary {
        text-shadow: none;
        }
    html body .user_reviews_filter_options {
        border: 1px solid rgba(255, 255, 255, 0.1);
        }
        html body .user_reviews_filter_score {
            border-top-color: rgba(255, 255, 255, 0.1);
            }
        html body .user_reviews_filter_score .game_review_summary:after {
            content: '- ' attr(data-tooltip-text);
            display: inline-block;
            vertical-align: middle;
            font-size: 11px;
