/*
 *  Wishlist
 *  ---------------------------------------------
 */

html body #wishlist_search {
    vertical-align: top;
    padding: 11px 10px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: none;
    }
    html body #wishlist_search:hover,
    html body #wishlist_search:active {
