    letter-spacing: 0.25em;
    box-sizing: border-box;
    }
    html body input.authcode_entry_input::-webkit-input-placeholder,
    html body input.twofactorauthcode_entry_input::-webkit-input-placeholder {
        letter-spacing: normal;
        font-size: 20px;
        text-align: center;
        }
    html body input.authcode_entry_input::-moz-placeholder,
    html body input.twofactorauthcode_entry_input::-moz-placeholder {
        letter-spacing: normal;
        font-size: 20px;
        text-align: center;
        }
html body .help_section_content,
html body .account_recovery_box {
    border: 1px solid rgba(255, 255, 255, 0.1);
