        background-color: #444 !important;
    }

    .oo-ui-buttonElement-button .oo-ui-iconElement-icon {
        -webkit-filter: invert(100%);
    }

    .oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button {
        background-color: #222 !important;
        border-color: #222 !important
    }

    div.mw-revslider-revision-wrapper-down.mw-revslider-revision-hovered {
        color: #d6a100 !important;
    }

    div.mw-revslider-revision-wrapper-up.mw-revslider-revision-hovered {
        color: #1e6db8 !important;
    }

    .mw-revslider-revision-older {
        background-color: #775900 !important;
    }

    .mw-revslider-revision-intermediate {
        background-color: #454545 !important;
    }

    .mw-revslider-revision-newer {
        background-color: #133656 !important;
    }

    .mw-revslider-revision {
        background-color: #555 !important;
    }

    del.diffchange.diffchange-inline {
        background-color: #775900 !important;
    }

    ins.diffchange.diffchange-inline {
        background-color: #133656 !important;
    }

    .diff-context {
        color: #999 !important;
    }

    td.diff-otitle,
    td.diff-ntitle {
        background-color: #333 !important;
    }

    .lang-list-button {
        outline-color: #222 !important;
    }

    .lang-list-content {
        background-color: #444 !important;
    }

    .bookshelf span {
        background-color: #444 !important;
    }

    .lang-list-container {
        background-color: #333 !important;
    }

    .localized-slogan {
        color: #555 !important;
    }

    .svg-Wikimedia-logo_black {
        -webkit-filter: invert(100%);
    }

    .hll {
        background-color: #2B2B2B !important;
    }

    kbd
{background-image: -webkit-linear-gradient(top, #333, #111, #333)!important;
    -webkit-box-shadow: 0.1em 0.2em 0.2em #111!important;
    border: 1px solid #555!important;}

div[style*="background:#ccc"]
{background:#333!important}
	.cta-holder{
		background-color:#333;}
	#NewarticletextNS>div{
		background-color:#333 !important;}
	select{
		/*selects are notoriousely hard to style, so I just put an 0.8 invert overt it. 
		  It looks fine I guess, but a better solution should be found*/
		filter:invert(0.8);}
	.oo-ui-textInputWidget .oo-ui-inputWidget-input{
		background-color:#444;
		color:#FFF;}
	.warningbox{
		background-color:#443;}
	/* Wikieditor */
	.wikiEditor-ui-toolbar{
		background-color:#444;}
	.wikiEditor-ui-toolbar .tabs span.tab a,
	.wikiEditor-ui-toolbar .tabs span.tab a:visited{
		background-image:url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 16 16"><path d="m5 4 4 4-4 4 1 1 5-5-5-5z" fill="%23FFF"/></svg>');}
	.mw-editform #wpTextbox1{
		background-color:#333;}
	.menuSwitcher{
		background-color:#333 !important;}
	.editMenus-button{
		background-color:#444;
		border:outset 2px #888;
		border-radius:0.25em;}
	.menuSwitcher-help-button,
	.menuSwitcher-help-button:hover,
	.menuSwitcher-hide-button,
	.menuSwitcher-hide-button:hover{
		color:#FFF !important;}
	.oo-ui-popupToolGroup-handle>span{
		filter:invert(1);}
	.oo-ui-popupToolGroup.oo-ui-widget-enabled > .oo-ui-popupToolGroup-handle:hover{
		background-color:#222;}

    /* Central Notice Banner */

    .cnotice {
        background-color: #333 !important;
        border: 1px solid #444;
    }

    .cnotice-toggle-box {
        -webkit-filter: invert(100%);
        filter: invert(100%);
    }

    div[style*="background:#FCFCFC"]
{background:#263F1E!important}

    /* Preferences */

    .client-js #preferences {
        border: 1px solid #c8ccd1 !important;
        background-color: #444 !important;
    }

    .client-js #preferences legend {
        color: #ededed;
    }

    /* German version */

    #hauptseite h2 {
        background: inherit;
        border: none;
    }

    #hauptseite .inhalt {
        background: inherit;
        border: none;
    }

    #qualitaetssicherungstext {
        background-color: #444 !important;
        border: 1px solid #666 !important;
    }

    /* French version */

    #accueil_2017_en-tete {
        background-image: none !important;
    }

    .bandeau-section {
        background-color: #333 !important;
        border: 1px solid #666 !important;
     }

    .autres-projets {
        background-color: #333 !important;
        border: 1px solid #666 !important;
    }

    /* Minor fixes */
    .app-badges .footer-sidebar-content {
        background-color: #222222 !important;
    }

    .mw-mmv-info-box {
        background-color: #111 !important;
    }

sourceCode:
/* ==UserStyle==
@name DuckDuckGo DeepDark
@namespace gitlab.com/RaitaroH/DuckDuckGo-DeepDark
@homepageURL https://gitlab.com/RaitaroH/DuckDuckGo-DeepDark
@version 1.9.3
@updateURL https://gitlab.com/RaitaroH/DuckDuckGo-DeepDark/raw/master/DuckDuckGoDeepDark.user.css
@description Light will blind you in your search for answers, dark will make them shine. May the dark be kinder on thine eyes. (DuckDuckGo dark theme)
@author RaitaroH
@license GNU-V3.0

@preprocessor stylus

@var select stylus-deepdark-style "Preset themes" [
	"Deep-Dark",
	"Inspired-Dark",
	"Breeze-Dark",
	"HavocOS",
	"Arc-Dark",
	"Vertex-Dark",
	"Mint-Y-Dark",
	"Adapta-Nokto",
	"Adapta-Breath-Nokto",
	"Gruvbox-Dark",
	"Gruvbox-Light",
	"NierAutomata-Dark",
	"NierAutomata-Light",
	"Solarized-Dark",
	"Solarized-Light",
	"9anime",
	"Firefox-Dark",
	"Firefox-57",
	"Discord",
	"YouTube-Dark",
	"Black-and-White",
	"Yellow",
	"Yellow-2",
	"Ubuntu-Grey",
	"Ubuntu-Purple",
	"Orange",
	"Jisho",
	"Custom"
]

@var color mainColor "Custom main color" "#367bf0"
@var color mainBackground "Custom main background" "#22242d"
@var color secondBackground "Custom second background" "#242730"
@var color hoverBackground "Custom hover background" "#4e5467"
@var color mainText "Custom main text" "#eee"
@var color dimmerText "Custom second text" "#ccc"
@var color colorShadow "Custom shadow color" "rgba(56, 60, 74, .3)"

==/UserStyle== */

@-moz-document regexp("https?:\/\/(?!help\.)(.*duckduckgo.com|3g2upl4pq6kufc4m.onion)\/(?!donations).*$") {

/*Theme made by RaitaroH @https://gitlab.com/RaitaroH/DuckDuckGo-DeepDark*/

/*GNU General Public License v3.0*/

	/*Main color variables*/
	:root
	{
		if stylus-deepdark-style == "Deep-Dark" {
			--main-color: #00adee;
			--main-background: #111;
			--second-background: #181818;
			--hover-background: #232323;
			--main-text: #eff0f1;
			--dimmer-text: #ccc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .3);
		}
		else if stylus-deepdark-style == "Inspired-Dark" {
			--main-color: #5e8acc;
			--main-background: #232629;
			--second-background: #181818;
			--hover-background: #515254;
			--main-text: #eee;
			--dimmer-text: #ccc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Breeze-Dark" {
			--main-color: #3daee9;
			--main-background: #232629;
			--second-background: #2a2e32;
			--hover-background: #31363b;
			--main-text: #eff0f1;
			--dimmer-text: #bdc3c7;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Vertex-Dark" {
			--main-color: #4080fb;
			--main-background: #2b2b2c;
			--second-background: #353638;
			--hover-background: #515254;
			--main-text: #f3f3f5;
			--dimmer-text: #aeafb0;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Arc-Dark" {
			--main-color: #5294e2;
			--main-background: #343944;
			--second-background: #383c4a;
			--hover-background: #414a59;
			--main-text: #c1c8d1;
			--dimmer-text: #b3bac5;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Firefox-Dark" {
			--main-color: #5675b9;
			--main-background: #272b35;
			--second-background: #181d20;
			--hover-background: #353a44;
			--main-text: #e3eef9;
			--dimmer-text: #bec0cc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Firefox-57" {
			--main-color: #4080fb;
			--main-background: #0c0c0d;
			--second-background: #252526;
			--hover-background: #323234;
			--main-text: #f9f9fa;
			--dimmer-text: #d0d0d0;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .2);
		}
		else if stylus-deepdark-style ==  "Discord" {
			--main-color: #7289da;
			--main-background: #1e2124;
			--second-background: #2f3136;
			--hover-background: #484b51;
			--main-text: #fff;
			--dimmer-text: #ada8aa;
			--shadow: 0 1px 0.5px rgba(47, 49, 54, .23);
		}
		else if stylus-deepdark-style == "YouTube-Dark" {
			--main-color: #e52117;
			--main-background: #111;
			--second-background: #232323;
			--hover-background: #343434;
			--main-text: #e1e1e1;
			--dimmer-text: #7f7f7f;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .2);
		}
		else if stylus-deepdark-style == "Mint-Y-Dark" {
			--main-color: #9ab87c;
			--main-background: #2f2f2f;
			--second-background: #383838;
			--hover-background: #404040;
			--main-text: #fff;
			--dimmer-text: #d5dada;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "9anime" {
			--main-color: #723f8c;
			--main-background: #0b0a0d;
			--second-background: #17151c;
			--hover-background: #1E1c25;
			--main-text: #f9f6fb;
			--dimmer-text: #cac0cf;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .13);
		}
		else if stylus-deepdark-style == "Black-and-White" {
			--main-color: #fff;
			--main-background: #000;
			--second-background: #1e1e1e;
			--hover-background: #313131;
			--main-text: #fff;
			--dimmer-text: #aaa;
			--shadow: 0 1px 0.5px rgba(54 ,54 ,54, .2);
		}
		else if stylus-deepdark-style == "Yellow" {
			--main-color: #ffc700;
			--main-background: #141414;
			--second-background: #202222;
			--hover-background: #353838;
			--main-text: #eff0f1;
			--dimmer-text: #9f9999;
			--shadow: 0 1px 0.5px rgba(34, 34, 34, .2);
		}
		else if stylus-deepdark-style == "Yellow-2" {
			--main-color: #ffc700;
			--main-background: #0a0800;
			--second-background: #0c0a04;
			--hover-background: #0f0d05;
			--main-text: #fffdf5;
			--dimmer-text: #fff8e1;
			--shadow: 0 1px 0.5px rgba(34, 34, 34, .2);
		}
		else if stylus-deepdark-style == "Ubuntu-Grey" {
			--main-color: #ef7847;
			--main-background: #312d2a;
			--second-background: #3d3c38;
			--hover-background: #59564d;
			--main-text: #f2f1ef;
			--dimmer-text: #e6e5e3;
			--shadow: 0 1px 0.5px rgba(89, 86, 77, .12);
		}
		else if stylus-deepdark-style == "Ubuntu-Purple" {
			--main-color: #ef7847;
			--main-background: #2c071a;
			--second-background: #430b28;
			--hover-background: #520D30;
			--main-text: #f2f1ef;
			--dimmer-text: #e6e5e3;
			--shadow: 0 1px 0.5px rgba(82, 13, 48, .2);
		}
		else if stylus-deepdark-style == "Orange" {
			--main-color: #ff6905;
			--main-background: #0a0400;
			--second-background: #0e0702;
			--hover-background: #110903;
			--main-text: #fff9f5;
			--dimmer-text: #ffede1;
			--shadow: 0 1px 0.5px rgba(255, 105, 5, .1);
		}
		else if stylus-deepdark-style == "Jisho" {
			--main-color: #ef7d6c;
			--main-background: #332222;
			--second-background: #2a1b1b;
			--hover-background: #863b2f;
			--main-text: #EFB26C;
			--dimmer-text: #986E3F;
			--shadow: 0 1px 0.5px rgba(37, 19, 5, .19);
		}
		else if stylus-deepdark-style == "Adapta-Nokto" {
			--main-color: #00bcd4;
			--main-background: #222d32;
			--second-background: #263238;
			--hover-background: #2a353b;
			--main-text: #fff;
			--dimmer-text: #9b9b9b;
			--shadow: 0 1px 0.5px  rgba(61, 77, 86, .2);
		}
		else if stylus-deepdark-style == "Adapta-Breath-Nokto" {
			--main-color: #1abc9c;
			--main-background: #222d32;
			--second-background: #263238;
			--hover-background: #2a353b;
			--main-text: #fff;
			--dimmer-text: #9b9b9b;
			--shadow: 0 1px 0.5px  rgba(42, 53, 59, .32);
		}
		else if stylus-deepdark-style == "Solarized-Dark" {
			--main-color: #268bd2;
			--main-background: #073642;
			--second-background: #03303c;
			--hover-background: #002b36;
			--main-text: #fdf6E3;
			--dimmer-text: #eee8d5;
			--shadow: 0 1px 0.5px  rgba(0, 43, 54, .3);
		}
		else if stylus-deepdark-style == "Solarized-Light" {
			--main-color: #268bd2;
			--main-background: #fdf6e3;
			--second-background: #f5efdc;
			--hover-background: #eee8d5;
			--main-text: #073642;
			--dimmer-text: #586e75;
			--shadow: 0 1px 0.5px  rgba(222, 216, 196, .2);
		}
		else if stylus-deepdark-style == "Gruvbox-Dark" {
			--main-color: #fe8019;
			--main-background: #1d2021;
			--second-background: #282828;
			--hover-background: #3c3836;
			--main-text: #fbf1c7;
			--dimmer-text: #ebdbb2;
			--shadow: 0 1px 0.5px  rgba(60, 56, 54, .22);
		}
		else if stylus-deepdark-style == "Gruvbox-Light" {
			--main-color: #af3a03;
			--main-background: #f9f5d7;
			--second-background: #fbf1c7;
			--hover-background: #ebdbb2;
			--main-text: #282828;
			--dimmer-text: #3c3836;
			--shadow: 0 1px 0.5px  rgba(235, 219, 178, .33);
		}
		else if stylus-deepdark-style == "NierAutomata-Dark" {
			--main-color: #fe8019;
			--main-background: #33302a;
			--second-background: #48453c;
			--hover-background: #7c6f64;
			--main-text: #dad4bb;
			--dimmer-text: #bab5a1;
			--shadow: 0 1px 0.5px  rgba(124, 111, 100, .15);
		}
		else if stylus-deepdark-style == "NierAutomata-Light" {
			--main-color: #fe8019;
			--main-background: #d1cdb7;
			--second-background: #dcd8c0;
			--hover-background: #bab5a1;
			--main-text: #48453c;
			--dimmer-text: #33302a;
			--shadow: 0 1px 0.5px rgba(186, 181, 161, 2);
		}
		else if stylus-deepdark-style == "HavocOS" {
			--main-color: #0794d4;
			--main-background: #141618;
			--second-background: #1c1e20;
			--hover-background: #212528;
			--main-text: #fff;
			--dimmer-text: #b9baba;
			--shadow: 0 1px 0.5px rgba(185, 186, 186, .04);
		}
		else if stylus-deepdark-style == "Custom" {
			--main-color: mainColor;
			--main-background: mainBackground;
			--second-background: secondBackground;
			--hover-background: hoverBackground;
			--main-text: mainText;
			--dimmer-text: dimmerText;
			--shadow: 0 1px 0.5px colorShadow;
		}
	}

	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/*Outline for links*/
	a
	{
		outline-color: var(--main-color) !important;
	}
	a:hover
	{
		text-decoration: none !important;
	}

	.svg .header__logo,.svg.dark-header .header__logo
	{
		background-image: url(https://gitlab.com//RaitaroH/DuckDuckGo-DeepDark/raw/master/Images/logo.png) !important;
	}

	.svg .logo_homepage
	{
		background-image: url(https://gitlab.com//RaitaroH/DuckDuckGo-DeepDark/raw/master/Images/BigLogo.png) !important;
